import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import AddProduct from './components/AddProduct';
import EditProduct from './components/EditProduct';
import Navbar from './components/Navbar';
import Home from './components/Home';
import About from './components/About';
import Login from './pages/Login';
import Register from './pages/Register';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if user is already logged in (e.g., by checking if a token exists in local storage)
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            isLoggedIn ? (
              <>
                <Navbar />
                <ProductList />
              </>
            ) : (
              <Navigate to="/login" replace={true} />
            )
          }
        />
        <Route
          path="/add"
          element={
            isLoggedIn ? (
              <>
                <Navbar />
                <AddProduct />
              </>
            ) : (
              <Navigate to="/login" replace={true} />
            )
          }
        />
        <Route
          path="/edit/:id"
          element={
            isLoggedIn ? (
              <>
                <Navbar />
                <EditProduct />
              </>
            ) : (
              <Navigate to="/login" replace={true} />
            )
          }
        />
        <Route
          path="/home"
          element={
            isLoggedIn ? (
              <>
                <Navbar />
                <Home />
              </>
            ) : (
              <Navigate to="/login" replace={true} />
            )
          }
        />
        <Route
          path="/about"
          element={
            isLoggedIn ? (
              <>
                <Navbar />
                <About />
              </>
            ) : (
              <Navigate to="/login" replace={true} />
            )
          }
        />
        <Route
          path="/login"
          element={<Login setIsLoggedIn={setIsLoggedIn} showNavbar={false} />}
        />
        <Route
          path="/register"
          element={<Register showNavbar={false} />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
