// App.js
import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import AddProduct from './components/AddProduct';
import EditProduct from './components/EditProduct';
import Navbar from './components/Navbar';
import Home from './components/Home';
import About from './components/About';
import Login from './pages/Login';
import Register from './pages/Register';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAuthPage, setIsAuthPage] = useState(false);

  useEffect(() => {
    // Check if user is already logged in (e.g., by checking if a token exists in local storage)
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <BrowserRouter>
      {isAuthPage ? null : <Navbar />}
      <Routes>
        <Route path="/" element={<ProtectedRoute isLoggedIn={isLoggedIn} setIsAuthPage={setIsAuthPage} />} />
        <Route path="/add" element={<AddProduct />} />
        <Route path="/edit/:id" element={<EditProduct />} />
        <Route path="/home" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} setIsAuthPage={setIsAuthPage} />} />
        <Route path="/register" element={<Register setIsAuthPage={setIsAuthPage} />} />
      </Routes>
    </BrowserRouter>
  );
}

const ProtectedRoute = ({ isLoggedIn, setIsAuthPage }) => {
  useEffect(() => {
    setIsAuthPage(false);
  }, [setIsAuthPage]);

  return isLoggedIn ? <ProductList /> : <Navigate to="/login" replace />;
};

export default App;
