import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './styles.css';

function Navbar() {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token"); // Hapus token dari localStorage
    navigate("/login"); // Redirect ke halaman login setelah logout
    window.location.reload(); // Memuat ulang halaman setelah logout
  };
  return (
    <nav className="nabar">
      <div className="navbar-brand">
        <h2 className="brand-text">Nama</h2>
        <h2 className="highlight-text">Toko</h2>
      </div>
      <input
        type="checkbox"
        id="sidebar-toggle"
        className="sidebar-toggle"
        checked={sidebarOpen}
        onChange={handleSidebarToggle}
      />
      <label htmlFor="sidebar-toggle" className="sidebar-toggle-label">
        <i className="fas fa-bars"></i>
      </label>
      <ul className={`nav-links ${sidebarOpen ? 'sidebar' : ''}`}>
        <li><Link to="/home">Home</Link></li>
        <li><Link to="/">Products</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link onClick={handleLogout}>Logout</Link></li>
      </ul>
    </nav>
  );
};

export default Navbar;
