import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import AddProduct from './components/AddProduct';
import EditProduct from './components/EditProduct';
import Navbar from './components/Navbar';
import Home from './components/Home';
import About from './components/About';
import Login from './pages/Login';
import Register from './pages/Register';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if user is already logged in (e.g., by checking if a token exists in local storage)
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  const PrivateRoute = ({ element, path }) => {
    return isLoggedIn ? element : <Navigate to="/login" />;
  };

  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
        <Route
          path="/login"
          element={!isLoggedIn ? <Login /> : <Navigate to="/" />}
        />
        <Route
          path="/register"
          element={!isLoggedIn ? <Register /> : <Navigate to="/" />}
        />
        <PrivateRoute path="/" element={<ProductList />} />
        <PrivateRoute path="/add" element={<AddProduct />} />
        <PrivateRoute path="/edit/:id" element={<EditProduct />} />
        <PrivateRoute path="/home" element={<Home />} />
        <PrivateRoute path="/about" element={<About />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
