import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import ProductList from './components/ProductList';
import AddProduct from './components/AddProduct';
import EditProduct from './components/EditProduct';
import Navbar from './components/Navbar';
import Home from './components/Home';
import About from './components/About';
import Login from './pages/Login';
import Register from './pages/Register';


function App() {
  const token = localStorage.getItem('token');
  const isLoggedIn = token ? true : false;

  return (
    <BrowserRouter>
      {isLoggedIn && <Navbar />}
      <Routes>
        <Route path="/" element={isLoggedIn ? <ProductList /> : <Navigate to="/login" />} />
        <Route path="/add" element={isLoggedIn ? <AddProduct /> : <Navigate to="/login" />} />
        <Route path="/edit/:id" element={isLoggedIn ? <EditProduct /> : <Navigate to="/login" />} />
        <Route path="/home" element={isLoggedIn ? <Home /> : <Navigate to="/login" />} />
        <Route path="/about" element={isLoggedIn ? <About /> : <Navigate to="/login" />} />
        <Route
          path="/login"
          element={isLoggedIn ? <Navigate to="/" replace={true} /> : <Login />}
        />
        <Route path="/register" element={isLoggedIn ? <Navigate to="/" replace={true} /> : <Register />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
